//react
import React, { useEffect, useRef, useState } from "react";
import { useRecoilState } from "recoil";
import ScrollToBottom from "react-scroll-to-bottom";
import {
    myUidState,
    opponentUidState,
    roomSeqState,
    opponentdataState,
    user1IdState,
    user2IdState,
    user1UIdState,
    user2UIdState,
	selectChatroomIdState
} from "../state/state";
import * as StompJs from "@stomp/stompjs";
import * as SockJS from "sockjs-client";

//css
import "./Chat.css";
import SendIcon from '@material-ui/icons/Send';
import axios from 'axios'
import { Badge } from '@material-ui/core';
// import ModalComponent from "../Openvidu/ModalComponent";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignOutAlt } from "@fortawesome/free-solid-svg-icons";

//openvidu
import Openvidu from '../Openvidu/Openvidu';
// openvidu css
import {Button,Modal} from "react-bootstrap"
import { faVideo } from "@fortawesome/free-solid-svg-icons";



function Chat() {
	// localstorage
	const client = useRef({});
	const jwttoken = 'Bearer ' + localStorage.getItem('token');
	const my_id = localStorage.getItem('user_id');
    const defaultData = {

        username:'Eoneo Bot', 
        userDetail:{
            // ? i I?
            profile_image:'1925626731595746.png'},
        userLanguage:{
            fluentLanguage:{language:'fluent'},
            nativeLanguage:{language:'native'},
            wantLanguage:{language:'want'},
        }
    }

	// usestate
	const [chatMessages, setChatMessages] = useState([]);
	const [message, setMessage] = useState('');
	const [mydata, setMydata] = useState({});
	const [chatrooms, setChatrooms] = useState([]);
	const [opponentdata, setOpponentdata] = useState(defaultData);
	// const [myUid, setMyUid] = useState("");
	
	//openvidu state
	const [fullscreen, setFullscreen] = useState(true);
    const [show, setShow] = useState(false);
    
	
	//recoildata
	const [selectChatroomId, setSelectChatroomId] = useRecoilState(selectChatroomIdState);
	const [RoomSeq, setRoomSeq] = useRecoilState(roomSeqState);
	const [myUid, setMyUid] = useRecoilState(myUidState);
	const [opponentUid, setOpponentUid] = useRecoilState(opponentUidState);

	// recoil only get
	const [user1Id, setUser1Id] = useRecoilState(user1IdState);
	const [user2Id, setUser2Id] = useRecoilState(user2IdState);
	const [user1UId, setUser1UId] = useRecoilState(user1UIdState);
	const [user2UId, setUser2UId] = useRecoilState(user2UIdState);

	useEffect(() => {
		getMyData();
		setRoomSeq(RoomSeq);
		selectChatroom(RoomSeq, user1Id, user1UId, user2Id, user2UId);
		getChatroomList();
		connect()
	}, []);

	useEffect(() => {
		dafaultcheck()
		getDBdata();
	
	}, [RoomSeq]);
	//공통 인증 헤더
	const config = {
		headers: { Authorization: jwttoken },
	};

	// websocket 연결
	const connect = () => {
		client.current = new StompJs.Client({
			// brokerURL: 'wss://api/chatEonoe-websocket',
			// webSocketFactory: () => new SockJS("/chatEonoe-websocket"), // proxy를 통한 접속 //internet explore
			webSocketFactory: () => new SockJS('/api/chatEonoe-websocket'),
			connectHeaders: { Authorization: jwttoken },
			debug: function (str) {
				console.log(str);
			},
			reconnectDelay: 5000,
			heartbeatIncoming: 4000,
			heartbeatOutgoing: 4000,
			onConnect: () => {
				subscribe();
			},
			onStompError: (frame) => {
				console.error(frame);
			},
		});
		client.current.activate();
	};

	const disconnect = () => {
		client.current.deactivate();
	};

	// 메시지 구독
	const subscribe = () => {
		client.current.subscribe(
			'/subscribe/' + myUid + '/queue/message',
			({ body }) => {
				setChatMessages((chatMessages) => [...chatMessages, JSON.parse(body)]);
			}
		);
	};

	//메시지 전송
	const publish = (message) => {
		if (!client.current.connected) {
			return;
		}
		let messagesdata = {
			chatRoomId: RoomSeq,
			sendUserId: mydata.id,
			message: message,
			sendUserUId: myUid,
			receiveUserUId: opponentUid,
		};

		client.current.publish({
			destination: '/publish/chat/message',
			body: JSON.stringify(messagesdata),
		});
	};



	//modal on function
	function handleShow2(event,breakpoint) {
		event.stopPropagation();
		publish(mydata.username+" invites you to video chat!")
		setFullscreen(breakpoint);
		setShow(true);
		}


	// props modal close function
    const closeModal = () => {
		setShow(false)
	  }

	// 내 정보 받기
	const getMyData = async () => {
		await axios
			.get(`/api/userinfo/${my_id}`, config)
			.then((response) => setMydata(response.data))
			// console.log(1,response))
			.catch((Err) => console.error(Err));
	};

	const getUserData = async (userId) => {
		await axios
			.get(`/api/userinfo/${userId}`, config)
			.then((response) => {
				setOpponentdata(response.data);
			})
			.catch((Err) => console.error(Err));
	};

	// 채팅메시지: 해당채팅방 메시지정보 불러오는 함수
	const getDBdata = async () => {
		await axios
			.get(`/api/chatroom/room/${RoomSeq}/`, config)
			.then(setChatMessages([]))
			.then((response) => {
				response.data.data.chats.map((chat, chatMessageId) =>
					setChatMessages((chatMessages) => [...chatMessages, chat])
				);
			})
			.then(disconnect)
			.then(connect)
			.catch((Err) => console.error(Err));
	};

	// 채팅방리스트: 현재 유저의 채팅방 리스트 불러오는 함수
	const getChatroomList = async () => {
		await axios
			.get(`/api/chatroom/rooms/` + my_id, config)

			.then(setChatrooms([]))
			.then((response) => {
				setChatrooms(response.data.data.chatRoomList);
			})

			.catch((Err) => console.error(Err));
	};

	// 채팅방삭제: 해당 채팅방을 제거하는 함수
	const deleteChatroom = async (chatRoomId, e) => {
		let data = {
			roomId: chatRoomId,
			userId: my_id,
		};
		console.log(data);
		await axios
			.put('/api/chatroom/room', data, config)
			.then(() => getChatroomList())
            .then(()=> setChatMessages([]))
            .then(()=> setOpponentdata(defaultData))
			.catch((Err) => console.error(Err));
	};

	// 채팅방 선택: 채팅방을 선택하는 함수
	const dafaultcheck = () => {
		if (RoomSeq === "0" ||"1") {
			console.log('check')
			setOpponentdata(defaultData)
		}
	}

	const selectChatroom = async (
		chatRoomId,
		user1Id,
		user1UId,
		user2Id,
		user2UId
	
	) => {
		setRoomSeq(chatRoomId);
		if (user1Id === parseInt(my_id)) {
			getUserData(user2Id);
			setMyUid(user1UId);
			setOpponentUid(user2UId);
		}
		else {
			getUserData(user1Id);
			setMyUid(user2UId);
			setOpponentUid(user1UId);
		}
		setSelectChatroomId(chatRoomId);
		console.log()
		console.log(chatRoomId,user1Id,user1UId,user2Id,user2UId)
	};

	return (
		<div>
			<h1>{selectChatroomIdState}</h1>
            <h1>{RoomSeq}</h1>
            <h1>{myUid}</h1> 
            <h1>{opponentUid}</h1> 

			<div id='frame'>
				{/* 사이드바 */}
				<div id='sidepanel'>
					{/* 1. 왼쪽 상단, 나의 프로필상태 */}
					<div id='profile'>
						<div className='wrap'>
							<p>Chats</p>
						</div>
					</div>

					{/* 2.왼쪽 중앙, 채팅방 */}
					<div id='contacts'>
						<ul style={{ paddingLeft: 0 }}>
							{chatrooms.map((chatroom, idx) => (
								<li
									key={idx}
									id='list-style'
									onClick={(e) => {
										selectChatroom(
											chatroom.chatRoomId,
											chatroom.user1Id,
											chatroom.user1UId,
											chatroom.user2Id,
											chatroom.user2UId
											// chatroom.user1Name,
											// chatroom.user2Name,
											// chatroom?.userDetail
										);
									}}
									className={
										chatroom.chatRoomId &&
										chatroom.chatRoomId === selectChatroomId
											? 'contact active'
											: 'contact'
									}>
									<div className='wrap' style={{ marginTop: 5, width: '100%' }}>
										<Badge
											style={{ float: 'left' }}
											overlap='circle'
											anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
											badgeContent={
												<img
													id='profileflag'
													src={chatroom.userDetail?.nationality?.flag}
													alt=''
												/>
											}>
											<img
												id='profileimg'
												src={
													'/static/img/' + chatroom.userDetail?.profile_image
												}
												alt=''
											/>
											{/* #123
                                            "data:image/jpeg;base64," /> */}
                                        </Badge>
                            
                                    <div className="meta" >
                                        {mydata.username === chatroom.user1Name 
                                            ?  <p className="name" style={{display: "inline-block"}}>{chatroom.user2Name}</p>
                                            :  <p className="name" style={{display: "inline-block"}}>{chatroom.user1Name}</p>
                                        }
                                
                                        <div style={{display: "inline-block",float: "right", marginRight:20}}>
                                            {chatroom.chatRoomId && chatroom.chatRoomId === selectChatroomId 
                                         
                                                ? <FontAwesomeIcon style= {{fontSize:25}} icon={faSignOutAlt} onClick={(e)=>{deleteChatroom(chatroom.chatRoomId, e)}} />
                                                : <p></p>
                                            }
                                        </div>
                                    </div>
                            </div>
                        </li>
                        ))}
                    </ul>
                </div>
            </div>
                {/* 중앙메시지 창 */}

                {/* 1. 중앙 왼쪽 상단에 상대의 사진과 이름 표기 */}
                <div className="content">

                    <div className="contact-profile">

                        <div style={{height:30}}>
							<img
								src={'/static/img/' + opponentdata?.userDetail?.profile_image}
							/>
							<p className='contact-profilename'>{opponentdata.username}</p>
							<div style={{ float: 'right', marginTop: 10 }}>
					
								<Button className="video-button" onClick={(e) => handleShow2(e,true)}>
									<FontAwesomeIcon id="videoicon"  icon={faVideo} />
									{typeof true === 'string' && `below ${true.split('-')[0]}`}
								</Button>
							
							</div>
						</div>

						<div class='chatlanguagebox'>
							<p id='chatnativelanguage'>
								{' '}
								{opponentdata?.userLanguage?.nativeLanguage?.language}
							</p>
							<p id='chatfluentlanguage'>
								{' '}
								{opponentdata?.userLanguage?.fluentLanguage?.language}
							</p>
							<p id='chatwantlanguage'>
								{' '}
								{opponentdata?.userLanguage?.wantLanguage?.language}
							</p>
						</div>
					</div>

					{/* 2. 메시지 배치
                    메시지전송유저 == 나: clsaa:sent(오른쪽배치)
                    메시지전송유저 != 나: class:replies(왼쪽배치)
                */}
                <ScrollToBottom className="messages" style={{height:'100'}}>
                    <ul style={{paddingLeft:0}}>
                        {chatMessages.map((msg) => (
                            <li style={{paddingRight: 10}} className={msg.sendUserId === mydata.id ? "sent" : "replies"}>
                                {msg.sendUserId !== mydata.id
                                ?(  <img src= {'/static/img/' +
                                     opponentdata?.userDetail?.profile_image} alt="" />
                                    )
                                :(  <img src= { '/static/img/' +
                                            mydata.userDetail?.profile_image} alt="" />
                                    )}
                                <p>{msg.message}</p>
                            </li>
                        ))}
                    </ul>
                </ScrollToBottom>

                {/* 3. 메시지 전송창 */}
                <div className="message-input">
                    <div className="wrap">
                        <input
                            name="user_input"
                            size="large"
                            
                            placeholder="Write your message..."
                            value={message}
                            onChange={(event) => setMessage(event.target.value)}
                            onKeyPress={(event) => {
                                if (event.key === "Enter") {
                                publish(message);
                                setMessage("");
                                }
                            }}
                        />
                    </div>

                {/* 4. 메시지 전송버튼 */}
                <button
                    id="chat-message-button"
                    style={{display:'inline-block' }}
                    onClick={(event) => {
                        publish(message);
                        setMessage("");
                    }}
                > <SendIcon/></button>
            </div>
        </div>
    </div>

        {/* modal */}
        <Modal id="openvidu-modal" show={show} fullscreen={fullscreen} onHide={() => setShow(false)}>
          {/* close button header */}
          <Modal.Header>
            <Modal.Title style={{textAlign:'center'}}>Video Chat</Modal.Title>
          </Modal.Header>
          <Modal.Body  >
            <Openvidu onClose={closeModal}/>
          </Modal.Body>
        </Modal>

</div>
    )
}

export default Chat;